import React, { Component } from "react";
import EmployeeCard from "./components/EmployeeCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import Sorting from "./components/Sorting";
import employees from "./employee.json";


class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    employees
  };

  removeFriend = id => {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    const employees = this.state.employees.filter(employees => employees.id !== id);
    // Set this.state.friends equal to the new friends array
    this.setState({ employees });
  };
  sortingByEmployeeFileNumber = () => {
    console.log("ok");
    const employees = this.state.employees.sort((a, b) => {
      if (a.employee_file_number < b.employee_file_number) {
        return -1;
      }
      if (a.employee_file_number > b.employee_file_number) {
        return 1;
      }
      return 0;
    })
    this.setState({ employees });
  }

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Title>Employee Profiles</Title>
        <Sorting
          onClick={this.sortingByEmployeeFileNumber}>Sorting by Employee File Number</Sorting>
        {this.state.employees.map(employees => (
          <EmployeeCard
            key={employees.id}
            id={employees.id}
            name={employees.name}
            occupation={employees.occupation}
            employee_file_number={employees.employee_file_number}
            office={employees.office}
            email={employees.email}
            phone_number={employees.phone_number}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
